mapper.select:
SELECT COUNT(*) FROM books
WHERE title = #{title} AND cinema_name = #{cinemaName} AND screen_hall_name = #{screenHallName} AND view_time = #{viewTime}
AND state_yn = 'Y'


SELECT docid, title, titleEng, SUBSTRING_INDEX(posters, '|', 1) AS posters, nation, runtime, rating, genre, repRlsDate, keywords, awards1, REPLACE(stlls, '|', ',') AS stlls
FROM movies
WHERE docid = #{docid}


SELECT plotText
FROM movies
WHERE docid = #{docid}


SELECT directorNm
FROM directors
WHERE docid = #{docid}
